import React from 'react';
import { Button, Form, FormGroup, Label, Input, Alert } from 'reactstrap';

class Forme extends React.Component {
    state = {
        mdp: '',
        email: ''
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitForm = e => {
        const user = {
            mdp:this.state.mdp,
            email: this.state.email
        }
        e.preventDefault()
        console.log(user)
        fetch('/auth/login', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'no-cors',
            body: user
        })
            .then(response => response.json())
            .then(item => {
                console.log(item)
            })
            .catch(err => console.log(err))
    }

    componentDidMount() {
        // if item exists, populate the state with proper data
        if (this.props.item) {
            const { email, mdp } = this.props.item
            this.setState({ email, mdp })
        }
    }

    render() {
        return (
            <div>
                <Alert color="danger" isOpen={true}></Alert>
                <Form onSubmit={this.submitForm}>
                    <FormGroup>
                        <Label for="email">Email</Label>
                        <Input type="email" name="email" id="email" onChange={this.onChange} value={this.state.email === null ? '' : this.state.email} />
                    </FormGroup>
                    <FormGroup>
                        <Label for="mdp">Mot de passe</Label>
                        <Input type="password" name="mdp" id="mdp" onChange={this.onChange} value={this.state.mdp === null ? '' : this.state.mdp} />
                    </FormGroup>
                    <Button>Submit</Button>
                </Form>
            </div>
        );
    }
}

export default Forme
