import React, { Component } from 'react';
import { Table,Button } from 'reactstrap';
import Forme from './Form';


class App extends Component {   
    state = {
        user: [],
        id: Forme.exportID
    }
    componentDidMount() {
        const { match: { params } } = this.props;
        fetch(`/use/users/${params.id}`, { credentials: 'include' })
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    var a = response.json();
                    console.log("1 : ", a);
                    a = a.then(Oject => console.log("2 : ", Oject.message))
                    console.log("2 : ", a.message);
                    throw new Error(a.message);
                }
            })
            .then(user => this.setState({ user }))
            .catch(err => {
                console.log(err); 
                //this.props.history.push("/login", {})
            })
    }
    Home = () => {
        this.props.history.push("/", {});
    };
    render() {
        const user = this.state.user.map(item => {
            return (
                <tr key={item.id}>
                    <th scope="row">{item.id}</th>
                    <td>{item.nom}</td>
                    <td>{item.prenom}</td>
                    <td>{item.email}</td>
                    <td>{item.mdp}</td>
                </tr>
            )
        })
        return (
            <div className="Unique User">
                <Table responsive hover>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>nom</th>
                            <th>prenom</th>
                            <th>Email</th>
                            <th>MDP</th>
                        </tr>
                    </thead>
                    <tbody>
                        {user}
                    </tbody>
                 
                </Table> 
                <Button
                    onClick={this.Home}
                    color="danger"
                    style={{ float: "left", marginRight: "5px" }}> Home
                </Button>
                {console.log(`${this.state.id}`,"id")}
            </div>
            )
    }
}

export default App
