import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody } from 'reactstrap'
import { BrowserRouter as Router, useHistory, Route, NavLink, Redirect, withRouter } from 'react-router-dom';
import AllUser from './App';

class app extends Component {
    
    
    ToAllUser = () => {
        this.props.history.push("/AllUsers", {});
    };
    GoUser = (id) => {
        this.props.history.push("/users/1", {});
    };
    render() {
        return (
            <Router forceRefresh={true}>
                <div class="home">
                    <body class="container">
                        <header class="page-header">
                            <h1>Pi carre Login</h1>
                            <a>Home</a>
                        </header>
                        <Button
                            onClick={this.ToAllUser}
                            color="info"
                            style={{ float: "right", marginRight: "10px"}}>clique ici bonhomme
                        </Button> 
                        <Button
                            onClick={() => this.GoUser(1) }
                            color="danger"
                            style={{ float: "right", marginRight: "5px" }}> ici...
                        </Button>  
                        <Button
                            onClick={() => this.GoUser(2)}
                            color="danger"
                            style={{ float: "right", marginRight: "5px" }}> ...ou la 
                        </Button>  
                    </body>
                </div>
                
            </Router>
        )
    }
}
export default app;
