import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';
import { BrowserRouter as Router } from 'react-router-dom';



class App extends Component {   
    state = {
        user: []
    }
    componentDidMount() {
        const { match: { params } } = this.props;
        fetch(`/use/users/${params.id}`, { credentials: 'include' })
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw response;
                }
            })
            .then(user => this.setState({ user }))
            .catch(err => {
                err.json().then(errMess => {
                    console.log("error : ", errMess.message);
                    if (errMess.message === "Un-Authorized User") {
                        this.props.history.push(`/users/${localStorage.user_id}`, {});
                        window.location.reload();
                    }
                    else {
                        this.props.history.push("/login", {})
                    }
                })                 
                
            })
    }
    Admin = () => {
        this.props.history.push("/allUsers", {});
    }
    Logout = () => {
        localStorage.removeItem('user_id');
        fetch("/auth/logout", { credentials: 'include' })
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw response;
                }
            })
            .then(res => {
                console.log(res.message)
                this.props.history.push("/login", {});
            })
            .catch(err => {
                err.json().then(errMess => {
                    console.log("error : ", errMess.message);
                })
            });        
    };
    render() {
        const user = this.state.user.map(item => {
            return (
                <tr key={item.id}>
                    <th scope="row">{item.id}</th>
                    <td>{item.nom}</td>
                    <td>{item.prenom}</td>
                    <td>{item.email}</td>
                    <td>{item.mdp}</td>
                </tr>
            )
        })
        return (
            <Router forceRefresh={true}>
                <div className="Unique User">
                    <Table responsive hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>nom</th>
                                <th>prenom</th>
                                <th>Email</th>
                                <th>MDP</th>
                            </tr>
                        </thead>
                        <tbody>
                            {user}
                        </tbody>
                 
                    </Table> 
                    <Button
                        onClick={this.Logout}
                        color="danger"
                        style={{ float: "left", marginRight: "5px" }}> Logout
                    </Button>
                    <Button
                        onClick={this.Admin}
                        //disabled={true}
                        outline={true}
                        color="danger"
                        style={{ float: "left", marginRight: "5px" }}> Admin Access
                    </Button>
                </div>
            </Router>
            )
    }
}

export default App
