import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody } from 'reactstrap'
import { BrowserRouter as Router, useHistory, Route, NavLink, Redirect, withRouter } from 'react-router-dom';
import AllUser from './App';

class app extends Component {
    
    Login = () => {
        this.props.history.push("/login", {});
    };
    ToAllUser = () => {
        this.props.history.push("/AllUsers", {});
    };
    GoUser = (id) => {
        this.props.history.push(`/users/${id}`, {});
    };
    render() {
        return (
            <Router forceRefresh={true}>
                <div class="home">
                    <body class="container">
                        <header class="page-header">
                            <h1>Pi carre Login</h1>
                        </header>
                        <Button
                            onClick={this.ToAllUser}
                            size="lg"
                            color="info"block
                            style={{ float: "center", marginRight: "10px"}}>tous les users
                        </Button> 
                        <Button
                            onClick={() => this.GoUser(1)}
                            size="lg"
                            color="danger"block
                            style={{ float: "center", marginRight: "5px" }}> user 1
                        </Button>  
                        <Button
                            onClick={() => this.GoUser(2)}
                            color="danger"
                            size="lg" block
                            style={{ float: "center", marginRight: "5px"}}> user 2
                        </Button>  
                        <Button
                            onClick={this.login}
                            color="danger"
                            size="lg" block
                            style={{ float: "center", marginRight: "5px" }}> Login
                        </Button>  
                    </body>
                </div>
                
            </Router>
        )
    }
}
export default app;
