import { BrowserRouter as Router, Route } from 'react-router-dom';
import React, { Component } from 'react'
import axios from 'axios'


class App extends Component {
    constructor(props) {
        super(props);
        this.state = { user: [] };   
    }
    componentDidMount() {
        const { match: { params } } = this.props;
        axios.get("http://localhost:5000/users/${params.id}")
            .then(({ data: user }) => {
                console.log('user', user);
                this.setState({ user })
            })
        
    }
    render() {
        const { user } = this.state;
        return (
            <Modal open dimmer="blurring">
                <Helmet>
                    <title>CMS | {user.name}</title>
                </Helmet>

                <Modal.Header>{user.nom}</Modal.Header>
                <Modal.Content image>
                     <Modal.Description>
                        <p>Email: {user.prenom}</p>
                        <p>Address: {user.mail}</p>
                        <p>City: {user.mdp}</p>
                    </Modal.Description>
                </Modal.Content>
                <Modal.Actions>
                    <Button positive>Edit</Button>
                    <Button>Close</Button>
                </Modal.Actions>
            </Modal>    
            )
    }
}

export default App
