import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody } from 'reactstrap'
import { BrowserRouter as Router, useHistory,Route, Link, Redirect, withRouter } from 'react-router-dom';
import AllUser from './App';

class app extends Component {
    
    state = {
        redirect :false
    }
    setRedirect = () => {
        this.setState({
            redirect:true
        })
    }
    renderRedirect = (lien) => {
        if (this.state.redirect) {
            return (
             <Router forceRefresh={true}>
                <Redirect to='/{lien}' />   
             </Router>
            )
        }
    }
    redirection = () => {
        this.setRedirect()
        
    }
    render() {
        
        return (
            <Router>
                <div class="home">
                    {this.renderRedirect()}
                    <body class="container">
                        <header class="page-header">
                            <h1>Pi carre Login</h1>
                            <a>Home</a>
                        </header>
                        <Button
                            color="info"
                            onClick={this.redirection}
                            style={{ float: "left", marginRight: "10px" }}>clique ici bonhomme
                        </Button>                        
                    </body>
                </div>
                
            </Router>
        )
    }
}
export default app;
